function find_floor(input::String)
    floor = 0
    for i in 1:length(input)
        current_instruction = input[i]
        
        if current_instruction == '('
            floor += 1
        end
        
        if current_instruction == ')'
            floor -= 1
        end
    end
    
    return floor
end

println(find_floor(input))

function find_santa_in_basement(input::String)
    floor = 0
    for i in 1:length(input)
        current_instruction = input[i]
        
        if current_instruction == '('
            floor += 1
        end
        
        if current_instruction == ')'
            floor -= 1
        end
        
        if floor == -1
            return i
        end
    end
    
    return nothing
end

println(find_santa_in_basement(input))
